mapper.insert:
insert into demand(
	demandnum
	, stdno
	, demandsubject

	, attend

	, startdate
	, enddate
	, demandcontent
	, classno
)
values(
	demand_seq.nextval
	, #{stdno}
	, #{demandsubject}

	, #{attend}

	, to_date(#{startdate}, 'MM/DD/YYYY')
	, to_date(#{enddate}, 'MM/DD/YYYY')
	, #{demandcontent}
	, #{classno}
)

mapper.select:
SELECT stdno
FROM   parent
WHERE  memno = #{memno}
